class Solution {
public:
    long long countSubarrays(vector<int>& v, int k) {
        int n = v.size();
        long long x = 0;

        map<long long, long long> mp;

        for (int i = 0; i < n; ++i) {
            map<long long, long long> a; 

            if (v[i] == k) {
                x++;
            }
            a[v[i]] = 1;

            for (auto it : mp) {
                long long res = it.first & v[i];
                if (res == k) {
                    x += it.second;
                }
                a[res] += it.second;
            }

            mp = a;
        }

        return x;
    }
};
