#include <vector>
#include <unordered_map>

class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long cnt = 0;
        unordered_map<long long, long long> mp;

        for (int i = 0; i < n; ++i) {
         unordered_map<long long, long long> mp1;

            if (nums[i] == k) {
                cnt += 1;
            }
            mp1[nums[i]] = 1;

            for ( auto& it : mp) {
                long long val = it.first;
                long long ans = (val & nums[i]);
                if (ans == k) {
                    cnt += it.second;
                }
                mp1[ans] += it.second;
            }

            mp = mp1;
        }

        return cnt;
    }
};
