class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
      int n=nums.size();
      long long cnt=0;


      unordered_map<long long,long long>prefix;

      for(int i=0;i<n;i++){
        unordered_map<long long ,long long>currResult;
        if(nums[i]==k){
            ++cnt;
        }
        currResult[nums[i]]=1;

       for(auto & [val,freq]:prefix){
        long long newResult=val & nums[i];
        if(newResult==k){
            cnt+=freq;
        }
        currResult[newResult]+=freq;
       }

       prefix=currResult;
      }
      return cnt;
    }
};
