#define ll long long
class Solution {
public:
    ll countSubarrays(vector<int>& nums, int target) {
        ll matches = 0;
        int n = nums.size(), fl = 0;

        unordered_map<ll, ll> previ, curre;

        for (int i = 0; i < n; i++) {
            curre.clear();
            curre[nums[i]] = 1;

            auto updateResults = [&]() {
                for (auto [combVal, occ] : previ) {
                    if ((combVal & nums[i]) == target) 
                        matches += occ;

                    curre[(combVal & nums[i])] += occ;
                }
            };
            if (nums[i] == target) {
                fl++;
                matches++;
            }


            updateResults();
            previ = move(curre);
        }

        return matches;
    }
};
