#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        priority_queue<pair<ll,ll>> pq;
        ll curr = nums[0];
        ll n = nums.size();
        ll res = 0;
        for(auto it : nums){
            if(it==k)res++;
        }
        for(int i = 0 ; i< n ;i++){
            priority_queue<pair<ll,ll>> curr;
            curr.push({nums[i],1});
            while(pq.size()> 0 && pq.top().first>=k){
                auto it = pq.top();
                int val = pq.top().first;
                int count = pq.top().second;
                pq.pop();
                while(pq.size()>0 && pq.top().first==val){count+=pq.top().second;pq.pop();}
                if((it.first&nums[i])==k){
                    res+=count;
                }
                curr.push({it.first&nums[i],count});
                // pq.pop();
            }
            pq= curr;
        }
        return res;
    }
};
