class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        a = []
        ans = 0
        for i, x in enumerate(nums):
            for p in a:
                p[0] &= x
            a.append([x, i])
            j = 0
            for p in a:
                if p[0] == a[j][0]:
                    a[j][1] = p[1]
                else:
                    j += 1
                    a[j] = p
            del a[j + 1:]
            right = bisect_left(a, k, key=lambda x:x[0])
            left = right - 1
            if right < len(a) and a[right][0] == k:
                ans += a[right][1] - (a[left][1] if left >= 0 else -1)
        return ans
