#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& ar, int k) {
        long long ans = 0;
        int n = ar.size();
        long long cnt = 0;
        vector<vector<int>> pre(n,vector<int>(61,0ll));
       unordered_map<ll,ll> mp;

        for(int i = 0; i < n; i++)
        {
            int ele = ar[i];
            if(k == ar[i]) cnt++;
            for(int j = 0; j < 61; j++)
            {
                if(ar[i]&(1ll<<j)) 
                {
                    pre[i][j] = 1;;
                    if(i != 0) pre[i][j] = pre[i-1][j];
                }
            }
            unordered_map<ll,ll> temp;
            temp[ar[i]]++;
            for(auto &it: mp)
            {
                // cout<<it.first<<" "<<it.second<<endl;
                int f = it.first, s = it.second;
                temp[(ele&f)] += s;
                int v = ele&f;
                if((ele & f) == k)
                {
                    // cout<<(ele&f)<<endl;
                    ans += s;
                    // cout<<f<<" "<<s<<endl;
                }
            }
            // for(auto it: temp)
            // {
            //     cout<<it.first<<" "<<it.second<<endl;
            // }
            mp = temp;
            // for(auto it: mp)
            // {
            //     cout<<it.first<<" "<<it.second<<endl;
            // }
        }
        ans += cnt;
        // cout<<ans<<endl;
                              return ans;

    }
};
