typedef long long ll;

class Solution {
public:
    ll countSubarrays(vector<int>& ar, int k) {
        int size = ar.size();
        ll subarrayCount = 0;
        unordered_map<ll, ll> previousResults;

        for (int index = 0; index < size; ++index) {
            unordered_map<ll, ll> currentResults;

            if (ar[index] == k) {
                ++subarrayCount;
            }
            currentResults[ar[index]] = 1;

            for (const auto& [value, frequency] : previousResults) {
                ll newAndResult = value & ar[index];
                if (newAndResult == k) {
                    subarrayCount += frequency;
                }
                currentResults[newAndResult] += frequency;
            }

            previousResults = move(currentResults);
        }

        return subarrayCount;
    }
};
