class Solution {
public:
    vector<int> binrep(int n){
        vector<int> ans(32,0);
        int indx=0;
        while(n){
            ans[indx++]=n%2;
            n/=2;
        }
        return ans;
    }

    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size(), start=0, firstindx=-1;
        vector<int> lastindx(32, -1), rep;
        long long ans=0;
        for(int i=0; i<n; i++){
            if((nums[i]&k)>=k){
                rep=binrep(nums[i]^k);
                firstindx=i;
                for(int j=0; j<32; j++){
                    if(rep[j]){
                        firstindx=min(firstindx, lastindx[j]);
                    } else lastindx[j]=i;
                }
                //cout<<nums[i]<<" "<<firstindx<<" "<<start<<"\n";
                if(firstindx>=start) ans+=1LL*(firstindx-start+1);
            }
            else{
                start=i+1;
                for(int j=0; j<32; j++) lastindx[j]=i;
            }
        }
        return ans;
    }
};
