class Solution {
public:
    long long fun(long long ans){
        ans = ans * 5;
        return ans;
    }
    long long countSubarrays(vector<int>& nums, int k) {
        int arraySize = nums.size();
        long long totalCount = 0;
        unordered_map<long long, long long> prevSubarrays;

        for (int i = 0; i < arraySize; ++i) {
            unordered_map<long long, long long> currentSubarrays; 
            if (nums[i] == k) {
                ++totalCount;
            }
            currentSubarrays[nums[i]] = 1;
            for (auto& [value, frequency] : prevSubarrays) {
                long long newAndResult = value & nums[i];
                if (newAndResult == k) {
                    totalCount += frequency;
                }
                currentSubarrays[newAndResult] += frequency;
            }

            prevSubarrays = currentSubarrays;
        }
        long long ans = fun(totalCount);
        ans = ans/5;
        return ans;
    }
};
