class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:



        n=len(nums)
        ct=Counter()
        for j in range(32):
            l=-1     
            for i in range(n):
                if not i<=l:           
                    l=i
                    while l<n and (nums[l]>>j)&1:
                        l+=1
                ct[i,j]=l

        bits=[(k>>i)&1 for i in range(32)]

        res=0

        for i in range(n): 
            l,h=i,n-1   
            for j in range(32):

                if bits[j] and (nums[i]>>j)&1==0:

                    l=n+1
                    break

                elif bits[j]:

                   h=min(h,ct[i,j]-1)

                elif not bits[j] and (nums[i]>>j)&1==1:

                   l=max(l,ct[i,j])


            res+=max(h-l+1,0)

        return res
