class Solution {
    public long countSubarrays(int[] nums, int k) {
        int nyuvi = nums.length,i=0;
        long yans = 0;
        HashMap<Long, Long> hm1 = new HashMap<>();

        while(i < nyuvi) {
            HashMap<Long, Long> hm2 = new HashMap<>(); 

            if (nums[i] == k) {
                yans++;
            }
            hm2.put((long) nums[i], 1L);

            for (var entry : hm1.entrySet()) {
                long checkans = entry.getKey() & (long) nums[i];
                if (checkans == k) {
                    yans += entry.getValue();
                }
                hm2.merge(checkans, entry.getValue(), Long::sum);
            }

            hm1 = hm2;
            i++;
        }

        return yans;
    }
}
