class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();long long cnt = 0;
        unordered_map<long long, long long> prevRe;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> currRe; 

            if (nums[i] == k) {
                ++cnt;
            }
            currRe[nums[i]] = 1;

            for (auto& [val, freq] : prevRe) {
                long long newAndResult = val & nums[i];
                if (newAndResult == k) {
                    cnt += freq;
                }
                currRe[newAndResult] += freq;
            }

            prevRe = currRe;
        }

        return cnt;
    }
};
