class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long count = 0;
        unordered_map<int, int> and_map;

        for (int num : nums) {
            unordered_map<int, int> new_and_map;

            if (num == k) {
                count++;
            }

            for (auto& pair : and_map) {
                int new_and = pair.first & num;
                if (new_and == k) {
                    count += pair.second;
                }
                new_and_map[new_and] += pair.second;
            }

            new_and_map[num]++;

            and_map = move(new_and_map);
        }

        return count;
    }
};
