impl Solution {
    pub fn count_subarrays(nums: Vec<i32>, k: i32) -> i64 {
        let mut ands: Vec<(i32, i64)> = vec![];
        let mut ret = 0;
        for &x in &nums {
            for (and, _) in ands.iter_mut() {
                *and &= x;
            }
            ands.push((x, 1));
            ands.dedup_by(|r, l| {
                if r.0 == l.0 {
                    l.1 += r.1;
                    true
                } else {
                    false
                }
            });
            for &(and, cnt) in &ands {
                if and == k {
                    ret += cnt;
                }
            }
        }
        ret
    }
}
