class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        c=0
        for i in range(len(nums)):
            temp=defaultdict(int)
            temp[nums[i]]=1
            if nums[i]==k: c+=1
            if i:
                for key,val in prev.items():
                    t=key&nums[i]
                    temp[t]+=val
                    if t==k: c+=val
            prev=temp        
        return c
