class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        unordered_map<long long, long long> mp;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> temp;

            if (nums[i] == k) {
                ans++;
            }

            temp[nums[i]] = 1;

            for (auto it : mp) {
                long long val=it.first;
                long long freq=it.second;
                long long curr = val & nums[i];
                if (curr == k) {
                    ans += freq;
                }
                temp[curr] += freq;
            }

            mp = temp;
        }

        return ans;
    }
};
