from collections import defaultdict

class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        count = 0
        p_res_132 = defaultdict(int)

        for i in range(n):
            c_res_132 = defaultdict(int)

            if nums[i] == k:
                count += 1
            c_res_132[nums[i]] = 1

            for val, freq in p_res_132.items():
                new_and_result = val & nums[i]
                if new_and_result == k:
                    count += freq
                c_res_132[new_and_result] += freq

            p_res_132 = c_res_132

        return count
