class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int len=nums.size();
        long long cnt=0;
        unordered_map<int,int> pre;
        for(int i=0;i<len;i++){
            unordered_map<int,int> cur;
            for(auto &[val,cnt]:pre){
                int newA=val&nums[i];
                cur[newA]+=cnt;
            }
            cur[nums[i]]++;
            if(cur.count(k)){
                cnt+=cur[k];
            }
            pre=cur;
        }
        return cnt;
    }
};
