class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int size = nums.size();
        long long count = 0;
        unordered_map<long long, long long> prev;
        for (int i = 0; i < size; ++i) {
            unordered_map<long long, long long> curr; 
            if (nums[i] == k) {
                ++count;
            }
            curr[nums[i]] = 1;
            for (auto& [value, freq] : prev) {
                long long result = value & nums[i];
                if (result == k) {
                    count += freq;
                }
                curr[result] += freq;
            }
            prev = curr;
        }
        return count;
    }
};
