class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long cnt = 0;
        unordered_map<int, int> curr, next;

        for (int i = 0; i < n; ++i) {
            next.clear();
            if (nums[i] == k) ++cnt;
            next[nums[i]] = 1;

            for (auto& [val, freq] : curr) {
                int andVal = val & nums[i];
                if (andVal == k) cnt += freq;
                next[andVal] += freq;
            }

            curr.swap(next);
        }

        return cnt;   
    }
};
