class Solution {
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        long count = 0;
        HashMap<Long, Long> prev = new HashMap<>();
        for (int num : nums) {
            HashMap<Long, Long> curr = new HashMap<>();
            if (num == k) {
                ++count;
            }
            curr.put((long) num, 1L);
            for (long val : prev.keySet()) {
                long newAnd = val & num;
                if (newAnd == k) {
                    count += prev.get(val);
                }
                curr.put(newAnd, curr.getOrDefault(newAnd, 0L) + prev.get(val));
            }
            prev = curr;
        }
        return count;
    }
}
