class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        int n = a.size(),maxi = -1e9;
        long long ans = 0;
        unordered_map<long long ,long long> mp;
        for(int i = 0;i<n;i++){
            unordered_map<long long ,long long> mpp;
            long long x = k;
            if(a[i] == x) ans++;
            mpp[a[i]] = 1;
            for(auto t : mp){
                long long p = (t.first) & a[i];
                if(p == x) ans += t.second;
                mpp[p] += t.second;
            }
            mp = mpp;
        }
        return ans;
    }
};
