#define ll long long
class Solution {
public:
    ll countSubarrays(vector<int> &nums, int k){
        ll n = nums.size(), count = 0;
        unordered_map<ll, ll> temp;

        for (int i = 0; i < n; ++i){
            unordered_map<ll, ll> m;
            count += (nums[i] == k);
            m[nums[i]] = 1;

            for (auto j : temp){
                ll ptr = nums[i] & j.first;
                if (ptr == k)
                    count += j.second;
                m[ptr] += j.second;
            }
            temp = m;
        }
        return count;
    }
};
