class Solution {
public:
    long long countSubarrays(vector<int>& abc, int k) {
        unordered_map<long long, long long> umap;
        int size = abc.size();
        long long total = 0;

        for (int i = 0; i < size; i++) {
            unordered_map<long long, long long> curr; 

            curr[abc[i]] = 1;
            if (abc[i] == k) {
                total++;
            }

            for (auto& [val, freq] : umap) {
                long long nw = val & abc[i];
                if (nw == k) {
                    total += freq;
                }
                curr[nw] += freq;
            }

            umap = curr;
        }
        return total;

    }
};
