class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long count = 0;

        unordered_map<long long,long long> prev;

        for(int i=0;i<n;i++)
        {
            unordered_map<long long,long long>curr;
            if(nums[i] == k)
            {
                count++;
            }
            curr[nums[i]] = 1;

            for(auto &[it,freq]: prev)
            {
                long long res = it & nums[i];
                if(res == k)
                {
                    count+= freq;
                }

                curr[res] += freq;
            }
            prev = curr;
        }
        return count;
    }
};
