class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long n = nums.size();
        long long ans = 0;
        map<long long, long long> mp;

         for (long long i : nums) {
            map<long long, long long> mp2;
           for (auto &it : mp) {
                long long y = it.first & i;
                mp2[y] += it.second;
                if (y == k) {
                    ans += it.second;
                }
            }
            mp2[i] += 1;
            if (i == k) {
                ans += 1;
            }
            mp = mp2;
        }

        return ans;
        }
};
