from typing import List
from collections import defaultdict

class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        size = len(nums)
        tot = 0
        prev_result = defaultdict(int)

        for index in range(size):
            curr_result = defaultdict(int)

            if nums[index] != k:
                curr_result[nums[index]] = 1
            else:
                tot += 1
                curr_result[nums[index]] = 1

            for value, freq in prev_result.items():
                rew_result = value & nums[index]
                if rew_result != k:
                    curr_result[rew_result] += freq
                else:
                    tot += freq
                    curr_result[rew_result] += freq

            prev_result = curr_result

        return tot
