class Solution(object):
    def countSubarrays(self, nums, k):
        size = len(nums)
        total = 0
        previous_results = defaultdict(int)
        for index in range(size):
            current_results = defaultdict(int)
            if nums[index] == k:
                total += 1
            current_results[nums[index]] = 1
            for value, frequency in previous_results.items():
                new_and_result = value & nums[index]
                if new_and_result == k:
                    total += frequency
                current_results[new_and_result] += frequency
            previous_results = current_results
        return total
