#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();

        unordered_map<ll, ll> mp1;

        ll result = 0;

        for (int i = 0; i < n; i++) {
            if (nums[i] == k) {
                result++;
            }

            unordered_map<ll, ll> mp2;
            mp2[nums[i]] = 1;
            for (auto it : mp1) {
                ll temp=(it.first & nums[i]);
                if ( temp== k) {
                    result += it.second;
                }
                mp2[temp] = mp2[temp] + it.second;
            }

            mp1 = mp2;
        }
        return result;
    }
};
