/**
 * @param {number[]} nums
 * @param {number} k
 * @return {number}
 */
var countSubarrays = function(nums, k) {
    let res = 0;
    let n = nums.length;

    let dp = new Map();
    dp.set(0, new Map());
    dp.get(0).set(nums[0], 1);
    if (nums[0] == k) res++;

    for (let i = 1; i < n; i++) {
        dp.set(i, new Map());
        dp.get(i).set(nums[i], 1);
        if (nums[i] == k) res++;

        for (let [key, count] of dp.get(i - 1)) {
            let temp = key & nums[i];
            if (!dp.get(i).has(temp)) dp.get(i).set(temp, 0);
            dp.get(i).set(temp, dp.get(i).get(temp) + count);
            if (temp == k) res += count;
        }
    }

    return res;
};
