class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long cnt = 0 ;
        unordered_map<int ,long long>mp;
        for(int i=0;i<n;i++){
            unordered_map<int , long long >m;
            if(nums[i] == k){
                cnt ++ ;
            }
            m[nums[i]] ++;
            for(auto it : mp){
                long long res = it.first & nums[i];
                m[res]+=it.second;
                if(res == k){
                    cnt += it.second;
                }
            }
            mp = m;
        }
        return cnt;
    }
};
