class Solution {
public:
    long long countSubarrays(vector<int>& arr, int target) {
        int n=arr.size();
        long long ans=0;
        unordered_map<long long, long long>mp;
        int i=0;
        while(i<n) {
            unordered_map<long long, long long>curr; 
            if (arr[i]==target) {
                ans++;
            }
            curr[arr[i]]=1;
            for (auto& [val,freq]:mp) {
                long long neww=val&arr[i];
                if (neww==target) {
                    ans+=freq;
                }
                curr[neww]+=freq;
            }
            mp=curr;
            i++;
        }
        return ans;
    }
};
