class Solution:
    def countSubarrays(self, A: List[int], k: int) -> int:
        def f(count, m):
            res = 0
            for i in range(30):
                if count[i] == m:
                    res += 1 << i
            return res

        def add(count, a, diff):
            for i in range(30):
                if a & (1 << i):
                    count[i] += diff

        def atLeast(k):
            count = [0] * 30
            i = 0
            res = 0
            for j,a in enumerate(A):
                add(count, a, 1)
                while i <= j and f(count, j - i + 1) < k:
                    add(count, A[i], -1)
                    i += 1
                res += j - i + 1
            return res
        return atLeast(k) - atLeast(k + 1)
