class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();

        long long total=0;

        map<long long,long long> mp1;
        int i=0;
        while(i<n){
            map<long long,long long> m2;
            if(nums[i]==k){
                total++;
            }

            m2[nums[i]]++;

            for(auto x: mp1){
                int val = x.first & nums[i];
                if(val == k){
                    total+=x.second;
                }

                m2[val]+=x.second;
            }

            mp1=m2;
            i++;
        }


        return total;
    }
};
