class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size(); long long ans = 0;
        map<long long, long long> pref;
        for(int i = 0; i < n; i++) {
            map<long long, long long> curr; 
            if(nums[i] != k) {
                ans = ans;
            } else {
                ans += 1;
            }
            curr[nums[i]] = 1;
            for (auto &[tmp, occ] : pref) {
                int mask = nums[i] & tmp;
                curr[mask] += occ;
                if(mask != k) {
                    continue;
                } else {
                    ans += occ;
                }
            }

            pref = curr;
        }
        return ans;
    }
};
