class Solution {
public:
    long long countSubarrays(std::vector<int>& nums, int k) {
        int n = nums.size();
        long long count = 0;
        std::unordered_map<int, int> player_scores;

        for (int i = 0; i < n; i++) {
            std::unordered_map<int, int> current_scores;
            if (nums[i] == k) {
                count++;
            }
            current_scores[nums[i]] = 1;

            for (auto& [score, cnt] : player_scores) {
                int result = score & nums[i];
                if (result == k) {
                    count += cnt;
                }
                if (current_scores.find(result) != current_scores.end()) {
                    current_scores[result] += cnt;
                } else {
                    current_scores[result] = cnt;
                }
            }

            player_scores = std::move(current_scores);
        }

        return count;
    }
};


