class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        unordered_map<long long, long long> prev;
        int i = 0;
        while(i < n){
            unordered_map<long long, long long> curr;
            if (nums[i] == k) {
                ans++;
            }
            curr[nums[i]] = 1;
            for (auto& it : prev) {
                long long val = it.first;
                long long freq = it.second;
                long long newAndResult = val & nums[i];
                if (newAndResult == k) {
                    ans += freq;
                }
                curr[newAndResult] += freq;
            }

            prev = curr;
            i++;
        }

        return ans;
    }
};
