class Solution {
public:
    #define ll long long
    long long countSubarrays(vector<int>& nums, int k) {
        ll res=0,n=nums.size();
        unordered_map<ll,ll>mp;
        for(ll i=0;i<n;i++)
        {
            unordered_map<ll,ll>nmp;
            for(auto pr:mp)
            {
                ll na=pr.first&nums[i];
                nmp[na]+=pr.second;
            }
            nmp[nums[i]]++;
            mp=nmp;
            if(mp.find(k)!=mp.end())
            {
                res+=mp[k];
            }
        }
        return res;
    }
};
