class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long countReqSubarray = 0;
        unordered_map<long long, long long> result_Prev;
        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> result_Crnt;
            if (nums[i] == k) {
                ++countReqSubarray;
            }

            result_Crnt[nums[i]] = 1;

            for (auto& [val, count] : result_Prev) {
                long long andLatest = val & nums[i];
                if (andLatest == k) {
                    countReqSubarray += count;
                }
                result_Crnt[andLatest] += count;
            }

            result_Prev = result_Crnt;
        }

        return countReqSubarray;
    }
};
