class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        ans = 0
        f = Counter()
        f[0] = 1
        for x in nums:
            f2 = Counter()
            f2[x] += 1
            for y in f:
                f2[x & y] += f[y]
            f = f2
            ans += f[k]
        if k == 0:
            ans -= len(nums)
        return ans
