class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        int n = nums.size();
        unordered_map<long long, long long> mp1;
        for(int i=0;i<n;i++){
            unordered_map<long long, long long> mp2;
            if(nums[i]==k){
                ans++;
            }
            mp2[nums[i]]++;
            for(auto it: mp1){
                int yash = (it.first & nums[i]);
                if(yash == k){
                    ans += it.second;
                }
                mp2[yash] += it.second;
            }
            mp1 = mp2;
        }
        return ans;
    }
};
