class Solution {
public:
    #define ll long long 
    ll f(vector<int> &v,ll k){
        ll r=-1,vAnd=INT_MAX,n=v.size(),ans=0;
        vector<ll> cnt(31,0);
        for(int l=0;l<n;l++){
            if(l>r){
                for(int j=0;j<31;j++){
                    if((v[l]&(1<<j))) cnt[j]++;
                }
                vAnd=v[l];
                r=l;
            }

            while(r<n && vAnd>k){
                r++;
                if(r<n){
                    vAnd=vAnd&v[r];
                    for(int j=0;j<31;j++){
                        if((v[r]&(1<<j))) cnt[j]++;
                    }
                }
            }

            ans+=r-l;

            for(int j=0;j<31;j++){
                if(!(v[l]&(1<<j)) && cnt[j]==r-l){
                    vAnd|=(1<<j);
                }
                if((v[l]&(1<<j))) cnt[j]--;
            }
        }

        return ans;
    }
    long long countSubarrays(vector<int>& nums, int k) {
        ll a=f(nums,k-1),b=f(nums,k);
        cout<<a<<" "<<b<<endl;
        return a-b;
    }
};
