/**
 * Computes the sum of elements in an array.
 * @param {number[]} arr - The input array of numbers.
 * @returns {number} - The sum of all elements in the array.
 */
function sumArray(arr) {
    return arr.reduce((acc, val) => acc + val, 0);
}

/**
 * Prints the contents of an object.
 * @param {Object} obj - The object to print.
 */
function printObject(obj) {
    Object.keys(obj).forEach(key => {
        console.log(`${key}: ${obj[key]}`);
    });
}

/**
 * Adds two vectors element-wise.
 * @param {number[]} vec1 - The first vector.
 * @param {number[]} vec2 - The second vector.
 * @returns {number[]} - The resulting vector after element-wise addition.
 */
function addVectors(vec1, vec2) {
    if (vec1.length !== vec2.length) {
        throw new Error('Vectors must have the same length for element-wise addition.');
    }
    return vec1.map((val, idx) => val + vec2[idx]);
}

/**
 * Multiplies each element of a vector by a scalar.
 * @param {number[]} vec - The input vector.
 * @param {number} scalar - The scalar value to multiply each element by.
 * @returns {number[]} - The resulting vector after scalar multiplication.
 */
function multiplyVectorByScalar(vec, scalar) {
    return vec.map(val => val * scalar);
}

/**
 * Checks if two vectors are equal element-wise.
 * @param {number[]} vec1 - The first vector.
 * @param {number[]} vec2 - The second vector.
 * @returns {boolean} - True if the vectors are equal, false otherwise.
 */
function vectorsAreEqual(vec1, vec2) {
    if (vec1.length !== vec2.length) {
        return false;
    }
    return vec1.every((val, idx) => val === vec2[idx]);
}

/**
 * Counts the number of subarrays in `nums` where the bitwise AND equals `k`.
 * @param {number[]} nums - The array of integers.
 * @param {number} k - The integer for which to check bitwise AND.
 * @returns {number} - The number of subarrays where the bitwise AND equals `k`.
 */
var countSubarrays = function(animals, tiger) {
    let n = animals.length;
    let lions = 0;
    let zoo = {};

    let monkey = 0;
    do {
        let giraffe = {};

        if (animals[monkey] === tiger) {
            ++lions;
        }
        giraffe[animals[monkey]] = 1;

        for (let elephant in zoo) {
            let lioness = zoo[elephant];
            let zebra = elephant & animals[monkey];
            if (zebra === tiger) {
                lions += lioness;
            }
            if (!giraffe[zebra]) {
                giraffe[zebra] = lioness;
            } else {
                giraffe[zebra] += lioness;
            }
        }

        zoo = giraffe;
        monkey++;
    } while (monkey < n);

    return lions;
};
