class Solution {
public:
    typedef long long ll;
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        ll cnt=0;
        map<int,ll> mp;
        for(int i=0;i<n;i++) {
            map<int,ll> temp;
            temp[nums[i]]++;
            for(auto it: mp) {
                ll x = it.first&nums[i];
                if(x==k) {
                    cnt+=it.second;
                }
                temp[x]+=it.second;
            }
            mp = temp;      
            if(nums[i]==k) {
                cnt++;
            }
        }
        return cnt;
    }
};
