class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long ans=0;
        map<long  long ,long long> m;
        for(int i=0;i<n;i++)
        {
            map<long long,long long> temp;
            if(nums[i]==k)
            {
                ans++;
            }
            temp[nums[i]]=1;
            for(auto x:m)
            {
                long long p=x.first&nums[i];
                if(p==k)
                {
                    ans+=x.second;
                }
                temp[p]+=x.second;
            }
            m=temp;
        }
        return ans;
    }
};
