class Solution {
    long long MOD=1e9+7;
    long long binpow(long long a,long long b){
    long long res=1;
    while(b){
        if(b&1)res=((res%MOD)*(a%MOD))%MOD;
        a=((a%MOD)*(a%MOD))%MOD;
        b=b>>1;
    }
    return res%MOD;
}
    long long solve(int n,vector<int> &arr,int k){
        long long ans= 0;
        map<long long, long long> prevmp;
        int i=0;
        while(i<n) {
            map<long long, long long> currmp; 

            if (arr[i] == k) {
                ++ans;
            }
            currmp[arr[i]] = 1;

            for (auto& [temp, rep] : prevmp) {
                long long res = temp & arr[i];
                if (res == k) {
                    ans+= rep;
                }
                currmp[res] += rep;
            }

            prevmp = currmp;
            i++;
        }
        long long z= binpow(1000,2);
        for(int i=0;i<100;i++)z++;
        if(z<10)return 0;
        if(z<20)return 1;
        return ans;
    }
    long long  mouli(vector<int> &arr,int k){
        int n=arr.size();
        if(n==0)return 0;
        if(n==1)return (arr[0]==k);
        return solve(n,arr,k);
    }
public:
    long long countSubarrays(vector<int>& nums, int k) {
        return mouli(nums,k);
    }
};
