class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        map<int, long long> prev;
        for(int i = 0; i < nums.size(); i++){
            map<int, long long> next;
            for(auto it:prev){
                next[it.first & nums[i]] += it.second;
            }
            next[nums[i]]++;
            prev = next;
            ans += next[k];
        }
        return ans;
    }   
};
