class Solution {
public:
    long long countSubarrays(vector<int>& arr,int t) {

        int n=arr.size();
        long long res=0;
        unordered_map<long long,long long>p;

        for(int i=0;i<n;i++) 
        {
            unordered_map<long long,long long>c;
            c[arr[i]]++;
            if(arr[i]==t) 
            res++;         
            for(auto a:p) 
            {
                long long temp=a.first&arr[i];

                if(temp==t) 
                res=res+a.second;
                c[temp]=c[temp]+a.second;
            }  
            p=c;
        }
        return res;
    }
};
