class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long ans=0;
        map<long long,long long>mp1;
        for(int i=0;i<n;i++){
            map<long long,long long>mp2;
            long long x=k;
            if(nums[i]==x)ans++;
            mp2[nums[i]]=1;
            for(auto it:mp1){
                long long y=it.first & nums[i];
                if(y==x)ans+=it.second;
                mp2[y]+=it.second;
            }
            mp1=mp2;
        }
        return ans;
    }
};
